class Solution {
public:
    vector <vector <pair <int, int>>> vect;    
    vector <pair <int, int>> vtt;

    int func (vector <int> &vt, int ind, int left, bool equal) 
    {
        if (left < 0) 
        {
            return 0;
        }

        if (equal) 
        {
            if (vect[ind][left].first != -1) 
            {
                return vect[ind][left].first;
            }
        }
        else 
        {
            if (vect[ind][left].second != -1) 
            {
                return vect[ind][left].second;
            }
        }

        int ans = 1;
        int n = vt.size();

        int num = 0;
        if (vtt[ind].first != -1) 
        {
            num = max (num, func (vt, vtt[ind].first, left - 1, true));
            num = max (num, func (vt, vtt[ind].first, left, false) - 1);
        }

        if (equal) 
        {
            if (vtt[ind].second != -1) 
            {
                num = max (num, func (vt, vtt[ind].second, left, true));
            }
        }

        ans += num;
        if (equal) 
        {
            vect[ind][left].first = ans;
        }
        else 
        {
            vect[ind][left].second = ans;
        }

        return ans;
    }

    int maximumLength(vector<int>& vt, int k) 
    {
        int n = vt.size();
        // vect.assign (n, {});

        vector <pair <int, int>> temp (n + 1, {-1, -1});
        for (int i = 0; i <= n; i++) 
        {
            vect.push_back (temp);
        }

        vtt.assign (n, {-1, -1});
        for (int i = 0; i < n; i++) 
        {
            for (int j = (i + 1); j < n; j++) 
            {
                if (vtt[i].first != -1 && vtt[i].second != -1) 
                {
                    break;
                }

                if (vt[j] != vt[i]) 
                {
                    if (vtt[i].first == -1) 
                    {
                        vtt[i].first = j;
                    }
                }
                else 
                {
                    if (vtt[i].second == -1) 
                    {
                        vtt[i].second = j;
                    }
                }
            }
        }

        int ans = 0;
        for (int i = 0; i < n; i++) 
        {
            ans = max (ans, func (vt, i, k, true));
            // func (vt, i, k - 1);
        }

        return ans;
    }
};
