class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int> > dp(n,vector<int> (k+1,0));
        for(int i=0;i<=k;i++){
            dp[0][i] = 1;
        }
        map<int,int> mp;
        for(int i=0;i<n;i++){
            mp[nums[i]] = -1;
        }
        mp[nums[0]] = 0;
        vector<int> maxi(k+1,1);
        for(int i=1;i<n;i++){
            for(int len=0;len<=k;len++){
                dp[i][len] = 1;
                if(mp[nums[i]] != -1){
                    dp[i][len] = max(dp[i][len] , 1 + dp[mp[nums[i]]][len]);
                }
                if(len > 0){
                    dp[i][len] = max(dp[i][len] , 1 + maxi[len-1]);
                }
                // for(int j=0;j<i;j++){
                //     else{
                //         if(len > 0){
                //             dp[i][len] = max(dp[i][len] , 1 + dp[j][len-1]);
                //         }
                //     }
                // }
            }
            for(int len = 0;len <= k;len++){
                maxi[len] = max(maxi[len] , dp[i][len]);
            }
            mp[nums[i]] = i;
        }
        int ans = 0;
        for(int i=0;i<n;i++){
            ans = max(ans , dp[i][k]);
        }
        return ans;
    }
};
