#include <vector>
#include <map> // Added for map usage
#include <algorithm>

using namespace std;

class Solution {
public:
    int maximumLength(vector<int>& v, int k) {
        int n = v.size(); 
        int ans = 0;
        vector<vector<int>> dp(n + 1, vector<int>(k + 1));
        vector<int> pmax(k + 1, 0);
        map<int, int> last;

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= k; j++) {
                dp[i][j] = pmax[j - 1] + 1;
            }
            int prev = last[v[i - 1]];
            for (int j = 0; j <= k; j++) {
                dp[i][j] = max(dp[i][j], dp[prev][j] + 1);
                pmax[j] = max(pmax[j], dp[i][j]);
                ans = max(ans, dp[i][j]);
            }
            last[v[i - 1]] = i;
        }
        return ans;
    }
};
