class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(k+1, 1));
        int ans = 1;
        vector<int> aaa(k+1, -1);
        vector<map<int, int>> aaam(k+1);

        for(int i = 0; i < n; i++) {
            for(int j = 0; j <= k; j++) {
                for(int l = 0; l < j; l++) {
                    dp[i][j] = max(dp[i][j], 1 + aaa[l]);
                }
                if(aaam[j].find(nums[i]) != aaam[j].end()) {
                    dp[i][j] = max(dp[i][j], 1 + aaam[j][nums[i]]);
                }
                ans = max(ans, dp[i][j]);
            }
            for(int j = 0; j <= k; j++) {
                aaa[j] = max(aaa[j], dp[i][j]);
                aaam[j][nums[i]] = max(aaam[j][nums[i]], dp[i][j]);
            }
        }
        return ans;
    }
};
