class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(k + 1, 1));
        int maxLength = 1;
        vector<int> maxLengths(k + 1, -1);
        vector<map<int, int>> lastOccurrences(k + 1);

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                if (lastOccurrences[j].find(nums[i]) != lastOccurrences[j].end()) {
                    dp[i][j] = max(dp[i][j], 1 + lastOccurrences[j][nums[i]]);
                }
                for (int l = 0; l < j; l++) {
                    dp[i][j] = max(dp[i][j], 1 + maxLengths[l]);
                }
                maxLength = max(maxLength, dp[i][j]);
            }

            for (int j = 0; j <= k; j++) {
                lastOccurrences[j][nums[i]] = max(lastOccurrences[j][nums[i]], dp[i][j]);
                maxLengths[j] = max(maxLengths[j], dp[i][j]);
            }
        }
        return maxLength;
    }
};
