 #define ll int
class Solution {
public:
     ll maximumLength(vector<ll>& nums, ll k) { 
 ll n=nums.size(); 
        vector<vector<ll>>dp(n+1,vector<ll>(k+1,1));
 ll ans=1; 
 vector<ll>dp1(k+1,-1); 
 vector<map<ll,ll>>dp2(k+1); 
 for(ll i=0;i<n;i++)
        { 
 for(ll j=0;j<=k;j++)
            { 
 for(ll l=0;l<j;l++) dp[i][j]=max(dp[i][j],1+dp1[l]); 
 if(dp2[j].find(nums[i])!=dp2[j].end()) dp[i][j]=max(dp[i][j],1+dp2[j][nums[i]]); 
 ans=max(ans,dp[i][j]); 
 } 
 for(ll j=0;j<=k;j++) dp1[j]=max(dp1[j],dp[i][j]),dp2[j][nums[i]]=max(dp2[j][nums[i]],dp[i][j]);   
 } 
 return ans; 
 }
};
