/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    let n = nums.length;
    if (n === 0) return 0;

    let dp = Array.from({ length: n }, () => Array(k + 1).fill(1));
    let ans = 1;
    let vk = Array(k + 1).fill(-1);
    let vkm = Array.from({ length: k + 1 }, () => ({}));

    for (let i = 0; i < n; i++) {
        for (let j = 0; j <= k; j++) {
            for (let l = 0; l < j; l++) {
                dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
            }
            if (vkm[j][nums[i]] !== undefined) {
                dp[i][j] = Math.max(dp[i][j], 1 + vkm[j][nums[i]]);
            }
            ans = Math.max(ans, dp[i][j]);
        }
        for (let j = 0; j <= k; j++) {
            vk[j] = Math.max(vk[j], dp[i][j]);
            vkm[j][nums[i]] = Math.max(vkm[j][nums[i]] || -1, dp[i][j]);
        }
    }

    return ans;
};
