class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
    int n=nums.size();
    vector<vector<int>> dp(n,vector<int>(k+1,1));
    int maxx=1;
    vector<int> maxxc(k+1,-1);
    vector<map<int, int>> maxwc(k + 1);
    for(int i=0;i<n;i++) {
        for(int j=0;j<=k;j++) {
            for (int t=0;t<j;t++) {
                dp[i][j]=max(dp[i][j],1+maxxc[t]);
            }
            if (maxwc[j].find(nums[i])!=maxwc[j].end()) {
                dp[i][j]=max(dp[i][j],1+maxwc[j][nums[i]]);
            }
            maxx=max(maxx,dp[i][j]);
        }
        for (int tt=0;tt<=k;tt++) {
            maxxc[tt]=max(maxxc[tt],dp[i][tt]);
            maxwc[tt][nums[i]]=max(maxwc[tt][nums[i]],dp[i][tt]);
        }
    }
    return maxx;
}
};
