class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dx = [[1] * (k + 1) for _ in range(n)]
        ans = 1
        ph = [-1] * (k + 1)
        hp = [{} for _ in range(k + 1)]
        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    dx[i][j] = max(dx[i][j], 1 + ph[l])
                if nums[i] in hp[j]:
                    dx[i][j] = max(dx[i][j], 1 + hp[j][nums[i]])
                ans = max(ans, dx[i][j])
            for j in range(k + 1):
                ph[j] = max(ph[j], dx[i][j])
                hp[j][nums[i]] = max(hp[j].get(nums[i], 0), dx[i][j])
        return ans
