from typing import List

class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [[1] * (k + 1) for _ in range(n)]
        ans = 1
        vk = [-1] * (k + 1)
        vkm = [{} for _ in range(k + 1)]

        for i in range(n):
            for j in range(k + 1):
                for l in range(j):
                    dp[i][j] = max(dp[i][j], 1 + vk[l])

                if nums[i] in vkm[j]:
                    dp[i][j] = max(dp[i][j], 1 + vkm[j][nums[i]])

                ans = max(ans, dp[i][j])

            for j in range(k + 1):
                vk[j] = max(vk[j], dp[i][j])
                vkm[j][nums[i]] = max(vkm[j].get(nums[i], 0), dp[i][j])

        return ans

# Example usage:
sol = Solution()
print(sol.maximumLength([1, 2, 1, 1, 3], 2))  # Output: 4
print(sol.maximumLength([1, 2, 3, 4, 5, 1], 0))  # Output: 2
