class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<int>best(k+1);
        vector<unordered_map<int,int>>mp(k+1);
        for(int i=0; i<nums.size(); i++)
        {
            for(int j=k;  j>=0; j--)
            {
                if(j<k)
                {
                    mp[j+1][nums[i]]=max(best[j]+1,mp[j+1][nums[i]]);
                    best[j+1]=max(best[j+1],mp[j+1][nums[i]]);
                }

                if(mp[j].find(nums[i])!=mp[j].end())
                {
                    mp[j][nums[i]]++;
                    best[j]=max(best[j],mp[j][nums[i]]);
                }
            }
            mp[0][nums[i]]=max(mp[0][nums[i]],1);
            best[0]=max(1,best[0]);
        }
        int ans=1;
        for(int i=0; i<=k; i++)
        {
            ans=max(best[i],ans);
        }
        return ans;
    }
};
