import java.util.*;

class Solution {
    public int maximumLength(int[] s, int g) {
        int d = s.length;
        int[][] dp = new int[d][g + 1];
        for (int i = 0; i < d; i++) {
            Arrays.fill(dp[i], 1);
        }

        int k = 1;
        int[] vk = new int[g + 1];
        Arrays.fill(vk, -1);

        @SuppressWarnings("unchecked")
        HashMap<Integer, Integer>[] vkm = new HashMap[g + 1];
        for (int i = 0; i <= g; i++) {
            vkm[i] = new HashMap<>();
        }

        int i = 0;
        while (i < d) {
            int j = 0;
            while (j <= g) {
                int l = 0;
                while (l < j) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
                    l++;
                }
                if (vkm[j].containsKey(s[i])) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vkm[j].get(s[i]));
                }
                k = Math.max(k, dp[i][j]);
                j++;
            }
            j = 0;
            while (j <= g) {
                vk[j] = Math.max(vk[j], dp[i][j]);
                vkm[j].put(s[i], Math.max(vkm[j].getOrDefault(s[i], 0), dp[i][j]));
                j++;
            }
            i++;
        }
        return k;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int[] nums = {1, 2, 2, 3, 1, 2};
        int k = 2;
        System.out.println(sol.maximumLength(nums, k)); // Example test
    }
}
