class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
    int n=nums.size();
    vector<vector<int>>dp(n,vector<int>(k+1,1));
    //vector<vector<int>>dp2(n,vector<int>(k+1,0));
    for(int j=0;j<=k;j++){
        dp[0][j]=1;
        //dp2[0][j]=1;
    }
    int ans=1;
    vector<int>vk(k+1,-1);
    vector<map<int,int>>vkm(k+1);
    for(int i=0;i<n;i++){
        for(int j=0;j<=k;j++){
            for(int l=0;l<j;l++){
                dp[i][j]=max(dp[i][j],1+vk[l]);
            }
            if(vkm[j].find(nums[i])!=vkm[j].end()){
                dp[i][j]=max(dp[i][j],1+vkm[j][nums[i]]);
            }
            ans=max(ans,dp[i][j]);
            //deb(i,j,dp[i][j]);
        }
        for(int j=0;j<=k;j++){
            vk[j]=max(vk[j],dp[i][j]);
            vkm[j][nums[i]]=max(vkm[j][nums[i]],dp[i][j]);
        }
    }
    return ans;
    }
};
