class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n=len(nums)
        dp=[[1]*(k+1) for i in range(n)]
        ans=1
        x=[-1]*(k+1)
        xm=[{} for i in range(k+1)]
        for i in range(n):
            for j in range(k+1):
                for l in range(j):
                    dp[i][j]=max(dp[i][j],1+x[l])
                if nums[i] in xm[j]:
                    dp[i][j]=max(dp[i][j],1+xm[j][nums[i]])
                ans=max(ans,dp[i][j])
            for j in range(k+1):
                x[j]=max(x[j],dp[i][j])
                if nums[i] not in xm[j]:
                    xm[j][nums[i]]=dp[i][j]
                else:
                    xm[j][nums[i]]=max(xm[j][nums[i]],dp[i][j])
        return ans
