class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        k1 = k+1
        glob = [0]*k1
        glob[0] = 1

        ch = nums[0]
        specific = {ch: glob.copy()}

        for x in nums[1:]:
            if x not in specific:
                specific[x] = [0]*k1
            old = specific[x]
            new = [0]*k1
            for i in range(k1):
                new[i] = max(old[i]+1, (glob[i-1]+1 if i>=1 else 0))
            specific[x] = new
            gg = [0]*k1
            for i in range(k1):
                gg[i] = max(new[i], (glob[i-1]+1 if i>=1 else 0))
            for i in range(k1):
                glob[i] = max(glob[i], gg[i])

        # print(glob)
        # print(specific)
        return max(glob)
