class Solution {
public:
    #define pii pair<int,int>
    #define fi first
    #define pb push_back
    #define si second
    #define all(o) o.begin(),o.end()

    int a[5005];
    int n;
  int dp[5005][52];
    int cdp[52];
    int mx[52];

    int maximumLength(vector<int>& nums, int k) {

        n=nums.size();

        map<int,int> mp;
        int cur=1;
        vector<int> t=nums;
        sort(all(t));
        for(auto x:t){
            if(mp[x]==0)
                mp[x]=cur++;
        }

        for(int i=0;i<n;i++)
            a[i+1]=mp[nums[i]];

    int ans=0;

    mp.clear();
    vector<int> vis;

        for(int i=1;i<=n;i++){


            for(int j=0;j<=k;j++)
                cdp[j]=dp[a[i]][j]+1;


            for(int j=1;j<=k;j++)
                cdp[j]=max(cdp[j],mx[j-1]+1);



            for(int j=0;j<=k;j++)
            {   dp[a[i]][j]=cdp[j];

                mx[j]=max(mx[j],dp[a[i]][j]);
                ans=max(ans,dp[a[i]][j]);
            }

            if(mp[a[i]]==0){
                vis.pb(a[i]);
                mp[a[i]]=1;
            }
        }
return ans;        
    }
};
