class Solution {
public:
    int maximumLength(vector<int>& values, int k) {
        int n=values.size();
        vector<vector<int>>dp(n+1,vector(k+1,0)); 
        unordered_map<int,unordered_map<int,int>>mp; 
        unordered_map<int,int>mp1;
        for(int i=0;i<=k;i++) dp[0][i]=1;
        int ans=0;
        for(int i=0;i<n;i++)
        {
            unordered_map<int,int>mp2=mp1;
            for(int j=0;j<=k;j++)
            {   

                dp[i][j]  = mp[values[i]][j]+1;
                if(j>0)
                    dp[i][j] = max(dp[i][j],mp2[j-1]+1);
                mp[values[i]][j] = max(mp[values[i]][j],dp[i][j]);

                mp1[j] = max(mp2[j],dp[i][j]);
                ans=max(ans,dp[i][j]);
            }

        }


        return ans;

    }
};

