class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        k++;
        vector<vector<int>> dp(nums.size(),vector<int> (k,0));

        vector<int> M(k,0);

        dp[0] = M;
        map<int,int> mp;

        for(int i = 0;i<nums.size();i++){
            int m = 0;
            for(int j = 0;j<k;j++){

                dp[i][j] = m+1;

                m = max(m,M[j]);    
            }

            if(mp.find(nums[i])!=mp.end()){
                int ind = mp[nums[i]];
                for(int j = 0;j<k;j++){
                    dp[i][j] = max(dp[i][j],1+dp[ind][j]);
                }
            }
            for(int j = 0;j<k;j++){
                M[j] = max(M[j],dp[i][j]);
            }
            mp[nums[i]] = i;

        }
        int ans = 0;

        for(auto &val:M) ans = max(ans,val);
        return ans;

    }
};
