function maximumLength(nums: number[], k: number): number {
    let max = 1;
    const m = new Array(nums.length).fill(0).map(t => new Array(k + 1).fill(0));
    let map = new Map();
    m[0][0] = 1;
    let maxes = new Array(k + 1).fill(0);

    for (let i = 0; i < m.length; ++i) {        
        for (let j = k; j >= 0; --j) {
            const eq = map.has(nums[i]) ? m[map.get(nums[i])][j] + 1 : 1;
            const uneq = j > 0 ? maxes[j - 1] + 1 : 1;

            m[i][j] = Math.max(m[i][j], eq, uneq);
            maxes[j] = Math.max(maxes[j], m[i][j]);
            max = Math.max(m[i][j], max);
        }

        map.set(nums[i], i);
    }

    return max;
};
