import java.util.*;

class Solution {
    public int maximumLength(int[] nums, int k) {
        int n = nums.length;
        int[][] dp = new int[n][k + 1];
        int ans = 1;
        int[] vk = new int[k + 1];
        Arrays.fill(vk, -1);
        List<Map<Integer, Integer>> vkm = new ArrayList<>();
        for (int j = 0; j <= k; j++) {
            vkm.add(new HashMap<>());
        }

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                dp[i][j] = 1;
                for (int l = 0; l < j; l++) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
                }
                if (vkm.get(j).containsKey(nums[i])) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vkm.get(j).get(nums[i]));
                }
                ans = Math.max(ans, dp[i][j]);
            }
            for (int j = 0; j <= k; j++) {
                vk[j] = Math.max(vk[j], dp[i][j]);
                vkm.get(j).put(nums[i], Math.max(vkm.get(j).getOrDefault(nums[i], 0), dp[i][j]));
            }
        }
        return ans;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();

        int[] nums1 = {1, 2, 1, 1, 3};
        int k1 = 2;
        System.out.println(solution.maximumLength(nums1, k1)); // Output: 4

        int[] nums2 = {1, 2, 3, 4, 5, 1};
        int k2 = 0;
        System.out.println(solution.maximumLength(nums2, k2)); // Output: 2
    }
}
