class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int size_nums = nums.size(); 
vector<vector<int>> dp(size_nums, vector<int>(k + 1, 1)); 
int max_answer = 1; 
vector<int> vk_values(k + 1, -1); 
vector<map<int, int>> vkm_values(k + 1); 
for(int index_i = 0; index_i < size_nums; index_i++){ 
    for(int index_j = 0; index_j <= k; index_j++){ 
        for(int index_l = 0; index_l < index_j; index_l++){ 
            dp[index_i][index_j] = max(dp[index_i][index_j], 1 + vk_values[index_l]); 
        } 
        if(vkm_values[index_j].find(nums[index_i]) != vkm_values[index_j].end()){ 
            dp[index_i][index_j] = max(dp[index_i][index_j], 1 + vkm_values[index_j][nums[index_i]]); 
        } 
        max_answer = max(max_answer, dp[index_i][index_j]); 
    } 
    for(int index_j = 0; index_j <= k; index_j++){ 
        vk_values[index_j] = max(vk_values[index_j], dp[index_i][index_j]); 
        vkm_values[index_j][nums[index_i]] = max(vkm_values[index_j][nums[index_i]], dp[index_i][index_j]); 
    } 
} 
return max_answer; 

    }
};
