class Solution {
public:
  int maximumLength(vector<int>& nomi, int k) {
    int n = nomi.size();
    vector<vector<int>> dp(n, vector<int>(k + 1, 1));
    int answer = 1;
    vector<int> xx(k + 1, -1);
    vector<map<int, int>> zzz(k + 1);

    for (int i = 0; i < n; i++) {
      for (int j = 0; j <= k; j++) {
        for (int l = 0; l < j; l++) {
          dp[i][j] = max(dp[i][j], 1 + xx[l]);
        }
        if (zzz[j].find(nomi[i]) != zzz[j].end()) {
          dp[i][j] = max(dp[i][j], 1 + zzz[j][nomi[i]]);
        }
        answer = max(answer, dp[i][j]);
      }

      for (int j = 0; j <= k; j++) {
        xx[j] = max(xx[j], dp[i][j]);
        zzz[j][nomi[i]] = max(zzz[j][nomi[i]], dp[i][j]);
      }
    }
    return answer;
  }
};
