 class Solution {
public:
    int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(),len=1; 
        vector<map<int,int>>store(k+1);
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        vector<int>arb(k+1,-1);  
        for(int i=0;i<n;i++)
        { 
            for(int j=0;j<=k;j++)
            { 
                for(int l=0;l<j;l++)  dp[i][j]=max(dp[i][j],arb[l]+1); 
                if(store[j].find(nums[i])!=store[j].end()) dp[i][j]=max(dp[i][j],store[j][nums[i]]+1); 
                len=max(len,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++)
            {  
                store[j][nums[i]]=max(store[j][nums[i]],dp[i][j]); 
                arb[j]=max(arb[j],dp[i][j]);
            } 
        } 
        return len; 
    }
 };
