class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

        int i,j,n=nums.size();
        map<int,vector<int>>mpp;
        map<int,int>vis;
        int dp1[k+1];
        for(i=0;i<=k;i++){
            dp1[i]=0;
        }
        int dp[n][k+1];
        for(i=0;i<n;i++){
            for(j=0;j<=k;j++){
                dp[i][j]=1;
            }
        }
        for(i=0;i<n;i++){
            for(j=1;j<=k;j++){
                 dp[i][j]=max(dp[i][j],dp1[j-1]+1);
}
            if(vis.find(nums[i])!=vis.end()){
                vector<int>d=mpp[nums[i]];
                 for(j=0;j<=k;j++){
                     dp[i][j]=max(dp[i][j],d[j]+1);
                 }
                d.clear();
                for(j=0;j<=k;j++){
                    d.push_back(dp[i][j]);
                }
                mpp[nums[i]]=d;
                       }
            else{
                vector<int>d;
                for(j=0;j<=k;j++){
                    d.push_back(dp[i][j]);
                }
                mpp[nums[i]]=d;
                vis[nums[i]]++;
                                  }
            for(j=0;j<=k;j++){
                dp1[j]=max(dp1[j],dp[i][j]);
            }
}
        int ans=0;
        for(i=0;i<n;i++){
            for(j=0;j<=k;j++){
                ans=max(ans,dp[i][j]);
            }
        }
        return ans;
    }
};
