class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int dp[5000][60] = {};
        int n = nums.size();
        int ans = 0;
        int maximo[60] = {};
        map<int, int> best[60];
        for (int i=0; i<n; i++){
            for (int j=0; j<=k; j++){
                dp[i][j] = 1;
                if (j != 0) dp[i][j] = max(dp[i][j], maximo[j-1]+1);
                dp[i][j] = max(dp[i][j], best[j][nums[i]]+1);
                ans = max(ans, dp[i][j]);
            }
            for (int j=0; j<=k; j++){
               maximo[j] = max(maximo[j], dp[i][j]);
                best[j][nums[i]] = max(best[j][nums[i]], dp[i][j]);
            }
        }
        return ans;
    }
};
