class Solution {
public:
    const int N = 300 + 2;
    const long long mod = 1e9 + 7;
    long long dp[302][302 + 100];
    int cnt[302], n;
    long long f(int i, int inv) {
        if(inv > cnt[n - 1]) return 0;
	    if(i >= n) return 1;

        long long &ret = dp[i][inv];
	    if(ret != -1) return ret;
	    ret = 0;
	    if(cnt[i] == -1) {
		    for (int j = 0; j <= i; j++) {
			    ret = (ret + f(i + 1, inv + j)) % mod;
		    }
	    }
	    else {
		    int tmp = cnt[i] - inv;
		    if(tmp >= 0 and tmp <= i) ret = f(i + 1, cnt[i]);
	    }
	    return ret;
    }

    int numberOfPermutations(int n1, vector<vector<int>>& req) {
        memset(dp, -1, sizeof(dp));
        memset(cnt, -1, sizeof(cnt));
        n = n1;
	    for (int i = 0; i < req.size(); i++) {
		    cnt[req[i][0]] = req[i][1];
	    }
	    return f(0, 0);
    }
};
