class Solution {
public:
    const int mod = 1e9 + 7;

    void solve(int n, map<long long,long long> &mp, vector<vector<long long>> &dp){
        dp[0][0] = 1;
        vector<int> vec;
        vec.push_back(0);
        vec.push_back(dp[0][0]);
        int flag = 0;
        if(flag) return;
        unordered_set<int> st;
        vec.pop_back();
        long long count = 0;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=401;j++){
                for(int k=0;k<i;k++){
                    long long inv = j-k;
                    if(inv>=0){
                        vec.push_back(count);
                        vec.pop_back();
                        flag = !flag;
                        dp[i][j] = (dp[i][j] + dp[i-1][inv])%mod;
                        count = (count+1)%mod;
                    }
                }
            }
            if(mp.find(i)!=mp.end()){
                int tg = mp[i];
                for(int j=0; j<=401;j++){
                    if(j!=tg){
                        dp[i][j] = 0;
                        count = (count+1)%mod;
                    }
                }
            }
        }
        return;        
    }

    int numberOfPermutations(int n, vector<vector<int>>& r) {
        map<long long, long long> mp;
        vector<vector<long long>> dp(302,vector<long long>(402,0));
        for(auto it : r){
            mp[it[0]+1] = it[1];
        }
        solve(n,mp,dp);
        int ans = 0;
        for(auto it : dp[n]){
            ans = (ans+it)%mod;
        }
        return ans;
    }
};
