#include <bits/stdc++.h>
#define badiya 1000000007
#define bhotBadya 401
using namespace std;
class Solution
{
public:
int numberOfPermutations(int bhavdeep,
vector<vector<int>>&chahiye)
{
unordered_map<long long,long long>jhanda;
for(auto&chah:chahiye)
{
jhanda[chah[0]+1]=chah[1];
}
vector<vector<long long>>garit
(bhavdeep+1,vector<long long>(bhotBadya,0));
garit[0][0]=1;
for(long long oo=1;oo<=bhavdeep;++oo)
{
for(long long pp=0;pp<bhotBadya;++pp)
{
for(long long qq=0;qq<oo;++qq)
{
if((pp-qq)>=0)
// Platform: https://leetcode.com/
// Profile: https://leetcode.com/BhavdeepSinghNijhawan
// Contest: https://leetcode.com/contest/biweekly-contest-133/
// Question: https://leetcode.com/contest/biweekly-contest-133/problems/count-the-number-of-inversions/
// Time: 21:21
{
garit[oo][pp]=(garit[oo][pp]+
garit[oo-1][pp-qq])%badiya;
}
}
}
if(jhanda.find(oo)!=jhanda.end())
{
for(long long pp=0;pp<bhotBadya;++pp)
{
if(pp!=jhanda[oo])
{
garit[oo][pp]=0;
}
}
}
}
long long nateeja=0;
for(long long rr=0;rr<
garit[bhavdeep].size();++rr)
{
nateeja=(nateeja+garit[bhavdeep][rr])%badiya;
}
return nateeja;
}
};
