class Solution {
public:

    int mod=1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& req) {
         map<int,int>tempmp;
        int maxi = 0;
        for(int i=0;i<req.size();i++)
        {
            int u=req[i][0]+1;
            int v=req[i][1];
            tempmp[u]=v;
        }
        for(int i=0;i<req.size();i++)
        {
            maxi=max(maxi,req[i][1]);
        }
        vector<vector<long long>>dp(n+1,vector<long long>(maxi+1,0));
        dp[0][0] = 1;
        for(int i = 1 ; i <= n ; i++){
            for(int j = 0 ; j <= maxi ; j++){
                int temp=dp[i-1][j];
                dp[i][j] = temp;
                for(int k = 1  ; k < i ; k++){
                    if(j - k < 0){
                        continue;
                    }
                    else
                    {
                        dp[i][j] = (dp[i][j]%mod + dp[i-1][j-k]%mod)%mod;
                        dp[i][j] %= mod;
                    }
                }
            }
            if(tempmp.count(i)){
                for(int j = 0 ; j <= maxi ; j++){
                    if(j==tempmp[i]){
                       continue;
                    }
                    else
                    {
                         dp[i][j] = 0;
                    }
                }
            }
        }
        long long res = 0;
        for(int i = 0 ; i <= maxi ; i++){
            if(dp[n][i]==0)continue;
            res = (res%mod + dp[n][i]%mod)%mod;
            res%=mod;
        }
        return res%mod;
    }
};
