#define ll long long int
const int MOD = 1e9+7;
class Solution {
private:

public:
    int numberOfPermutations(int n, vector<vector<int>>& reqs) {
        unordered_map<int, int> mp;
        for (auto it : reqs) mp[it[0] + 1] = it[1];
        vector<vector<ll>> dp(n + 1, vector<ll>(400 + 1, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= 400; j++) {
                for (int k = 0; k < i; k++) {;
                    if (j - k >= 0) dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % MOD;
                }
            }
            if (mp.count(i)) {
                for (int c = 0; c <= 400; c++) {
                    if (c != mp[i]) dp[i][c] = 0;
                }
            }
        }
        ll ans = 0;
        for (ll it : dp[n]) {
            ans = (ans + it) % MOD;
        }
        return ans;
    }
};
