class Solution {
    public:
    int numberOfPermutations(int n, vector<vector<int>>& req) {

        int mod = 1000000007, mx = 400;
        map<int, int> mp;
        vector<vector<long long>> cnt(n+1, vector<long long>(mx + 1, 0));
        for (vector<int> r : req)
            mp[r[0] + 1] = r[1];
        cnt[0][0] = 1;

        for (int i=1; i<=n; i++){
            for (int inv=0; inv<=mx; inv++){
                for (int j=0; j<i; j++){
                    if (inv >= j)
                        cnt[i][inv] = (cnt[i][inv] + cnt[i-1][inv-j])%mod;
                }
            }

            if (mp.find(i)!=mp.end()){
                for (int inv=0; inv<=mx; inv++){
                    if (inv != mp[i])
                        cnt[i][inv] = 0;
                }
            }
        }

        long long ans = 0;
        for (long long it:cnt[n])
            ans = (ans + it)%mod;
        return (int)ans;
    }
};
