class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& a) {
        sort(a.begin(),a.end());
        for(int i=1;i<a.size();i++) if(a[i][1]<a[i-1][1]) return 0;
        if(a.back()[1]==0) return 1;
        if(a.back()[1]>n*(n-1)/2) return 0;
        long long M=1e9+7,k=a.back()[1];
        vector<vector<long long>> dp(n+1,vector<long long>(k+1,0));
        dp[0][0]=1;
        for(int i=1,t=0;i<=n;i++){
            if(!t||!a[t-1][1]) dp[i][0]=1;
            for(int j=1;j<=k;j++){
                dp[i][j]=(dp[i][j-1]+dp[i-1][j])%M;
                if(j>=i) dp[i][j]=(dp[i][j]-dp[i-1][j-i]+M)%M;
            }
            if(t<a.size()&&a[t][0]==i-1){
                for(int j=0;j<=k;j++) if(j!=a[t][1]) dp[i][j]=0;
                t++;
            }
        }
        return dp[n][k];
    }
};
