using ll =long long;
class Solution {
public:
    vector<vector<ll>>dp;
    const int mod=1e9+7;
    const int N= 405;
    map<ll,ll>mp;
    void help(ll n)
    {
        for (ll i = 1; i <= n; i++) {
            for (int k = 0; k <= 402; k++) {
                for (int j = 0; j < i; j++) {
                    int temp_k = k - j;
                    if(temp_k<0)continue;
                    dp[i][k] = (dp[i][k] + dp[i - 1][temp_k]) % mod;
                }
            }
            if (mp.count(i)) {
                int k_to_get = mp[i];
                for (int k = 0; k <= 402; k++) {
                    if (k != k_to_get) {
                        dp[i][k] = 0;
                    }
                }
            }
        }
    }
    int numberOfPermutations(int n, vector<vector<int>>& r) {

        for (auto re:r)
        {
            mp[re[0]+1]=re[1];
        }

        dp.resize(n+2,vector<ll>(N,0));
        dp[0][0]=1;

        help(n);



        ll ans=0;
        for(auto &x:dp[n])
        {
            ans=(ans+x)%mod;
        }
        return ans;
    }
};
