#define MOD 1000000007
class Solution {
public:
    vector<vector<int>> dp;
    int numberOfPermutations(int n, vector<vector<int>>& arr) {
        vector<int> arr1(405,-1);
        for(auto it:arr){
            arr1[it[0]+1]=it[1];
        }
        dp.resize(n+1,vector<int>(405,0));
        dp[0][0]=1;
        for(int i=0;i<n;i++){
            for(int j=0;j<=400;j++){
                for(int k=0;k<=min(i,j);k++){
                        dp[i+1][j]=(dp[i+1][j]+dp[i][j-k])%MOD;
                }
            }
            if(arr1[i+1]!=-1){
                for(int j=0;j<arr1[i+1];j++){
                     dp[i+1][j]=0;
                }
                for(int j=arr1[i+1]+1;j<=400;j++){
                    dp[i+1][j]=0;
                }
            }
        }
        long long ans=0;
        for(int i=0;i<=400;i++){
            ans=(ans+dp[n][i])%MOD;
        }
        return ans;
    }
};
