class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        M = 10**9 + 7
        maxinvv = 500

        a=0
        f=True
        dp = [[0] * (maxinvv + 1) for _ in range(n + 1)]
        dp[0][0] = 1
        dictii = {x[0] + 1: x[1] for x in requirements}

        # for i in range(1, n + 1):
        #     for j in range(n * (n + 1) // 2 + 1):
        #         for k in range(i):
        #             if j >= k:
        #                 dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % MOD

        for i in range(1, n + 1):
            for j in range(maxinvv + 1):
                for k in range(i):
                    if f:
                        p = j - k
                        if p >= 0:
                            a+=1
                            t = (dp[i][j] + dp[i - 1][p]) % M
                            dp[i][j]=t
                    else:
                        a=0
                # print(a)

            if f and i in dictii:
                ttinv = dictii[i]
                for j in range(maxinvv + 1):
                    if j != ttinv:
                        a+=j
                        dp[i][j] = 0
                    else:
                        a-=1
            # print(a)
        if f:
            anss = sum(dp[n]) % M
        # else:
        #     a

        return anss
