class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        MOD=10**9+7
        reqList=[-1]*(n+1)
        for req in requirements:
            reqList[req[0]+1]=req[1]

        maxInversions=max(reqList)

        @cache
        def dp(i,k):
            if k<0:
                return 0
            if i==0:
                if k==0:
                    return 1
                return 0
            if reqList[i]!=-1 and k!=reqList[i]:
                return 0

            res=0
            for j in range(i):
                res=(res+dp(i-1,k-j))%MOD
            return res

        res=0
        for inversion in range(maxInversions+1):
            res=(res+dp(n,inversion))%MOD

        return res
