#define MOD 1000000007
typedef long long ll;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& rq) {
        vector<vector<ll>> v(n + 1, vector<ll>(401, 0));
        v[0][0] = 1;
        map<int, int> mp;
        int ans = 0;
        for (int i = 0; i < rq.size(); i++) {
            mp[rq[i][0] + 1] = rq[i][1];
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < 401; j++) {
                for (int k = 0; k < i; k++) {
                    if (j >= k) {
                        v[i][j] = (v[i][j] + v[i - 1][j - k]) % MOD;
                    }
                }
            }
            if (mp.count(i) > 0)
                for (int j = 0; j < 401; j++)
                    if (mp[i] != j) v[i][j] = 0;
        }
        for (int i = 0; i < v[n].size(); i++) {
            ans = (ans + v[n][i]) % MOD;
        }
        return ans;
    }
};
