class Solution {


public:
    int MOD = 1e9+7;
    int numberOfPermutations(int n, vector<vector<int>>& v) {

        unordered_map<int, int> um;
        for (auto &it : v) {
            um[it[0] + 1] = it[1]; 
        }

        vector<vector<long long>> dp(n + 1, vector<long long>(401, 0));
                // dp[0][0] = 0;
        dp[0][0] = 1;

        int qq = 1;
        while (qq <= n) {
            int c1 = 0;
            while (c1 <= 400) {
                int it2=0,db=0;
                while (it2 < qq) {

                    if (c1 - it2 >= 0) {


                        // dp[qq][c1] = (dp[qq][c1]% MOD + dp[qq][c1]% MOD) % MOD;
                        dp[qq][c1] = (dp[qq][c1]% MOD + dp[qq - 1][c1 - it2]% MOD) % MOD;
                        // dp[qq][c1] = (dp[qq][c1] + dp[qq][c1-it2]) % MOD;
                        db++;
                    }
                    it2++;
                }
                c1++;
            }

            if (um.count(qq) > 0) {
                int c2 = 0;
                while (c2 <= 400) {
                    int da=0;
                    if (c2 !=  um[qq]) {
                        dp[qq][c2] = 0;
                        da++;
                    }
                    c2++;
                }
            }

            qq++;
        }

        long long ans = 0,c3 = 0;
        while (c3 <= 400) {
            ans = (ans% MOD + dp[n][c3]% MOD) % MOD;
            c3++;
        }

        return ans%MOD;
    }
};
