class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> req(n, -1);
        for (vector<int> &v : requirements)
            req[v[0]] = v[1];

        int MOD = 1000000007;
        vector<vector<int>> dp(n, vector<int>(401));
        for (int i = 0; i < n; i++)
        {
            if (i == 0)
                dp[i][0] = 1;
            else
                for (int j = 0; j <= 400; j++)
                    for (int k = 0; k <= min(i, j); k++)
                        dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % MOD;

            if (req[i] != -1)
                for (int j = 0; j <= 400; j++)
                    if (j != req[i])
                        dp[i][j] = 0;
        }
        return dp[n - 1][req[n - 1]];
    }
};
