class Solution {
public:

    #define mod (1000000000+7);

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {

        int maxi=400;

        unordered_map<int,int>m;

        for(auto i:requirements){

            m[i[0]+1]=i[1];

        }

         vector<vector<long long>> count(n + 1, vector<long long>(maxi + 1, 0));

        count[0][0]=1;

        for(int l=1;l<=n;l++){

            for(int inv=0;inv<=maxi;inv++){

                for(int pos=0;pos<l;pos++){

                    int prev=inv-pos;

                    if(prev>=0){

                        count[l][inv]=(count[l][inv]+count[l-1][prev])%mod;


                    }
                }
            }

            if(m.find(l)!=m.end()){

                int target=m[l];

                for(int inv=0;inv<=maxi;inv++){
                    if(inv!=target){
                        count[l][inv]=0;
                    }
                }
            }

        }

        long  res=0;

        for( long i:count[n]){

            res=(res+i)%mod;

        }

        return int(res);


    }
};
