MOD = 10**9 + 7
class Solution:
    def numberOfPermutations(self, n: int, requirements: List[List[int]]) -> int:
        maxi = 400
        end = {}
        for req in requirements:
            end[req[0] + 1] = req[1]

        dp = [[0] * (maxi + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(1,n+1):
            for j in range(maxi+1):
                for k in range(i):
                    l = j - k
                    if l >= 0:
                        dp[i][j] = (dp[i][j] + dp[i - 1][l]) % MOD

            if i in end:
                req = end[i]
                for j in range(maxi+1):
                    if j != req:
                        dp[i][j] = 0
        ans = 0
        for i in range(maxi+1):
            ans = (ans + dp[n][i]) % MOD
        return ans
