class Solution {
public:
    int mod = 1e9 + 7;
    long long dp[301][401];
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        long long res = 0;
        unordered_map<int, int> mp;
        for(auto &req: requirements)
            mp[req[0] + 1] = req[1];
        dp[0][0] = 1;
        for(int i = 1; i <= n; i++){
            for(int j = 0; j <= 400; j++){
                for(int k = 0; k < i; k++){
                    if (j - k >= 0) 
                        dp[i][j] = (dp[i][j] + dp[i - 1][j - k]) % mod;
                }
            }
            if(mp.find(i) != mp.end()){
                for(int j = 0; j <= 400; j++){
                    if(j != mp[i])
                        dp[i][j] = 0;
                }
            }
        }
        for(int i = 0; i <= 400; i++) 
            res = (res + dp[n][i]) % mod;
        return res;
    }
};

