class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& r) {
        int s=r.size(),m=1e9+7,ans=0;
        vector<int>mi(305,-1);
         for(int i=0;i<s;i++){
            int x=r[i][0]+1,y=r[i][1];
            mi[x]=y;
        }
        vector<vector<int>>u(n+1,vector<int>(405,0)),v(n+1,vector<int>(405,0));
        u[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<401;j++){
                for(int x=0;x<i;x++){
                    if(x<=j) u[i][j]=(u[i][j]+u[i-1][j-x])%m;
                }
            }
            if(mi[i]!=-1){
                for(int j=0;j<401;j++){
                    if(mi[i]==j) continue;
                    u[i][j]=0;
                }
            }
        }

         for(int j=0;j<401;j++){
              ans=(ans+u[n][j])%m;
            }
        return ans;
    }
};
