/**
 * @param {number} n
 * @param {number[][]} requirements
 * @return {number}
 */
var numberOfPermutations = function(n, requirements) {
    const max = 401;
    const mod = 1e9 + 7;
    requirements.sort((a, b) => a[0] - b[0]);
    let arrCnt = Array(max).fill(0);
    if (requirements[0][0] === 0) {
        if (requirements[0][1] !== 0) {
            return 0;
        }
    }
    arrCnt[0] = 1;
    let pref = Array(max).fill(1);
    let idx = 0;
    if (requirements[0][0] === 0) {
        idx = 1;
    }
    for (let j = 1; j < n; j += 1) {
        const newArr = Array(max).fill(0);
        const newPr = Array(max).fill(0);
        for (let i = 0; i < arrCnt.length; i += 1) {
            newArr[i] = (pref[i] - (pref[i - j - 1] ?? 0)) % mod;
            newPr[i] = (newPr[i - 1] ?? 0) + newArr[i];
        }
        if (requirements[idx][0] === j) {
            const cnt = requirements[idx][1];
            arrCnt = Array(max).fill(0);
            arrCnt[cnt] = newArr[cnt];
            pref = Array(max).fill(0).fill(newArr[cnt], cnt);
            idx += 1;
        } else {
            arrCnt = newArr;
            pref = newPr;
        }
        //console.log('pppp', arrCnt, pref)
    }
    return pref.at(-1) % mod;
};
