private:

    static const int MAX = 401;
    static const int dhruvMOD = 1'000'000'007;

public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<long long>> permCountkrlo(n + 1, vector<long long>(MAX + 1, 0));
        permCountkrlo[0][0] = 1;
        unordered_map<int, int> tyagiji;
        for (vector<int>& dhruvtyagi : requirements) {
            tyagiji[dhruvtyagi[0] + 1] = dhruvtyagi[1];
        }





        for (int lengthlagna = 1; lengthlagna <= n; lengthlagna++) {
            for (int inv = 0; inv <= MAX; inv++) {
                for (int newPos = 0; newPos < lengthlagna; newPos++) {
                    int prevInv = inv - newPos;
                    if (prevInv >= 0) {
                        permCountkrlo[lengthlagna][inv] = (permCountkrlo[lengthlagna][inv] + permCountkrlo[lengthlagna - 1][prevInv]) % dhruvMOD;
                    }
                }
            }



            if (tyagiji.count(lengthlagna)) {
                int targetInv = tyagiji[lengthlagna];
                for (int inv = 0; inv <= MAX; inv++) {
                    if (inv != targetInv) {
                        permCountkrlo[lengthlagna][inv] = 0;
                    }
                }
            }
        }

        long long jawab = 0;
        for (long long count : permCountkrlo[n]) {
            jawab = (jawab + count) % dhruvMOD;
        }



        return static_cast<int>(jawab);
    }


