const int MAX_CNT=402;

class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<int> req(n, NOREQ);
        for(auto v: requirements) {
            const int end=v[0], cnt=v[1];
            req[end]=cnt;
        }

        vector<vector<ll>> dp(n+1, vector<ll>(MAX_CNT, 0));
        dp[0][0]=1;

        for(int sz=0; sz<n; sz++) {
            FOR(prev_inv, MAX_CNT) {
                const ll dp_old=dp[sz][prev_inv];

                for(int pos=0; pos<=sz; pos++) {
                    const int new_inv=min(prev_inv+pos, MAX_CNT-1);
                    add(dp[sz+1][new_inv], dp_old);

                }
            }
            const int need=req[sz];
            if(need!=NOREQ) {
                FOR(i, MAX_CNT) if(i!=need) dp[sz+1][i]=0;
            }

        }

        ll result=0;
        FOR(inv, MAX_CNT) add(result, dp[n][inv]);
        return result;
    }
};
