class Solution {
public:
    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        vector<vector<int>>dp(n+1,vector<int>(401,0));
        dp[0][0] = 1;
        long long ans = 0;

        int MOD = 1000000007;

        unordered_map<int,int>mp;
        for(int i=0;i<requirements.size();i++){
            mp[requirements[i][0]+1] = requirements[i][1];
        }

        for(int i=1;i<=n;i++){
            for(int inv=0;inv<=400;inv++){
                for(int j=0;j<i;j++){
                    if(inv>=j){
                        dp[i][inv] = (dp[i][inv]+dp[i-1][inv-j])%MOD;
                    }
                }
            }
            if(mp.count(i)>0){
                for(int j=0;j<=400;j++){
                    if(j!=mp[i]){
                        dp[i][j] = 0;
                    }
                }
            }
        }

        for (auto it : dp[n]) {
            ans = (ans + it) % MOD;
        }  

        return ans;
    }
};
