class Solution {
public:



    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        int mod= 1e9+7;
        map<int,int> m;
        set<int> st;

        for(auto it: requirements){
            st.insert(it[0]+1);
            m[it[0]+1]= it[1];
        }

        vector<vector<long long>> dp(n+1, vector<long long> (401, 0));
        dp[0][0]=1;
        for(int i=1;i<=n;i++){
            for(int j=0;j<401;j++){
                for(int k=0;k<i;k++){
                    if(j-k>=0){
                        dp[i][j]= (dp[i][j] + dp[i-1][j-k])%mod;
                    }
                }
            }
            if(st.find(i)==st.end()){
                continue;
            }
            int t= m[i];
            for(int j=0;j<401;j++){
                if(t!=j){
                    dp[i][j]=0;
                }
            }

        }

        int ans=0;

        for(int i=0;i<401;i++){
            ans= (ans+ dp[n][i])%mod;;
        }
        return ans;
    }
};
