#define ll long long int
#define mod 1000000007

class Solution{
public:
    int numberOfPermutations(int n, vector<vector<int>> &r){

        map<int, int> mp;
        for(auto it: r){
            mp[it[0]+1]=it[1];
        }

        vector<vector<ll>> dp(n+1, vector<ll> (401, 0));
        dp[0][0]=1;
        for(int i=1; i<=n; i++){
            for(int j=0; j<=400; j++){
                int p=0;
                while(p<i){
                    int pidx=j-p;
                    if(pidx>=0){
                        dp[i][j] = (dp[i][j]+dp[i-1][pidx])%mod;   
                    }
                    p++;
                }
            }

            if(mp.find(i) != mp.end()){
                for(int j=0; j<=400; j++){
                    if(j != mp[i]){
                        dp[i][j]=0;
                    }
                }
            }
        }

//          for(int i=0; i<=n; i++){
//             for(int j=0; j<=2; j++){
//                 cout << dp[i][j] << " ";      
//             }

//              cout << endl;
//          }


        ll res=0;
        for(auto it: dp[n]){
            // cout << it << endl;
            res = (res+it)%mod;
        }



        return res;
    }
};
