class Solution {
public:
    const int MOD = 1e9+7;

    int numberOfPermutations(int n, vector<vector<int>>& requirements) {
        sort(requirements.begin(),requirements.end());
        int idx = 0;
        long long dp[305][405], qs[405];
        memset(dp,0,sizeof(dp));
        dp[0][0] = 1;
        qs[0] = dp[0][0];
        for (int j = 1; j <= 400; ++j) {
            qs[j] = qs[j-1]+dp[0][j];
            qs[j] %= MOD;
        }
        if (requirements[idx][0] == 0) {
            if (requirements[idx][1] != 0) {
                return 0;
            }
            ++idx;
        }
        long long ans = 0;
        for (int i = 1; i < n; ++i) {
            if (requirements[idx][0] == i) {
                int x = requirements[idx][1];
                dp[i][x] = qs[x]-(x-i-1 >= 0 ? qs[x-i-1] : 0)+MOD;
                dp[i][x] %= MOD;
                ans = dp[i][x];
                ++idx;
            } else {
                for (int j = 0; j <= 400; ++j) {
                    dp[i][j] = qs[j]-(j-i-1 >= 0 ? qs[j-i-1] : 0)+MOD;
                    dp[i][j] %= MOD;
                }
            }
            qs[0] = dp[i][0];
            for (int j = 1; j <= 400; ++j) {
                qs[j] = qs[j-1]+dp[i][j];
                qs[j] %= MOD;
            }
        }
        return ans;
    }
};
